export const GEMINI_MODEL = 'gemini-2.5-flash-native-audio-preview-12-2025';

export const getAccentAdjustedInstruction = (accent: string, config: BusinessConfig, isPersonalized: boolean = false) => {
  const baseInstruction = `
You are "Amar Voice AI," a polite and highly efficient customer service assistant for an e-commerce business in Bangladesh.
Your primary language of communication is Bengali (Bangla).

Business Context:
- Shop Name: "${config.shopName}"
- Delivery: 1-2 days inside Dhaka (à§³${config.deliveryInsideDhaka}), 3-5 days outside Dhaka (à§³${config.deliveryOutsideDhaka}).
- bKash: ${config.bkashNumber}.
- Tone: ${config.personaTone}

LEAD GENERATION TASK:
- If a customer mentions their name, phone number, or address, you MUST call the "save_lead" function to store it for the merchant.
- Politely confirm that you've saved their details so a manager can contact them.

VISUAL ADVISOR MODE:
- If the user sends an image, analyze it carefully. If it's a product, describe its features in Bengali and tell the user if it matches your shop's categories.

Guidelines:
1. Speak in Bengali. Use polite forms (Apni/Apnar).
2. Keep responses concise for voice.
3. Use regional greetings if appropriate for the dialect.
`;

  let accentGuideline = "";
  switch (accent) {
    case 'Chittagong':
      accentGuideline = "Speak in Chittagonian (Chatgaya) dialect and accent. Use regional melodic patterns.";
      break;
    case 'Sylhet':
      accentGuideline = "Speak in Sylheti dialect and accent. Use regional vocabulary.";
      break;
    case 'Dhaka':
    default:
      accentGuideline = "Use Standard Shuddho Bengali (Dhaka-centric) formal accent.";
      break;
  }

  return `${baseInstruction}\n\nDIALECT: ${accentGuideline}`;
};

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { GoogleGenAI, Modality, Type } from '@google/genai';
import { decode, decodeAudioData, createBlob, playTone } from '../services/audioUtils';
import { GEMINI_MODEL, getAccentAdjustedInstruction } from '../constants';
import { BusinessConfig, Lead } from '../types';

interface VoiceInterfaceProps {
  onClose: () => void;
  config: BusinessConfig;
  onLeadCaptured?: (lead: Lead) => void;
}

type ConnectionStatus = 'Idle' | 'Initializing' | 'Connecting' | 'Listening' | 'Processing' | 'Speaking' | 'Error';

const VoiceInterface: React.FC<VoiceInterfaceProps> = ({ onClose, config, onLeadCaptured }) => {
  const [isActive, setIsActive] = useState(false);
  const [status, setStatus] = useState<ConnectionStatus>('Idle');
  const [transcriptions, setTranscriptions] = useState<{role: string, text: string}[]>([]);
  const [isRecording, setIsRecording] = useState(false);
  const [isSendingImage, setIsSendingImage] = useState(false);

  const audioContextRef = useRef<{ input: AudioContext; output: AudioContext; } | null>(null);
  const sessionPromiseRef = useRef<Promise<any> | null>(null);
  const nextStartTimeRef = useRef<number>(0);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const stopSession = useCallback(() => {
    sessionPromiseRef.current?.then(s => s.close?.()).catch(() => {});
    sessionPromiseRef.current = null;
    audioContextRef.current?.input.close().catch(() => {});
    audioContextRef.current?.output.close().catch(() => {});
    audioContextRef.current = null;
    setIsActive(false);
    setStatus('Idle');
  }, []);

  const handleImageUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file || !sessionPromiseRef.current) return;

    setIsSendingImage(true);
    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64Data = (reader.result as string).split(',')[1];
      const session = await sessionPromiseRef.current;
      session.sendRealtimeInput({
        media: { data: base64Data, mimeType: file.type }
      });
      setIsSendingImage(false);
      playTone(audioContextRef.current!.output, 880, 0.1);
    };
    reader.readAsDataURL(file);
  };

  const startSession = async () => {
    if (isActive) return;
    setStatus('Initializing');
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const inputCtx = new (window.AudioContext || (window as any).webkitAudioContext)({ sampleRate: 16000 });
      const outputCtx = new (window.AudioContext || (window as any).webkitAudioContext)({ sampleRate: 24000 });
      audioContextRef.current = { input: inputCtx, output: outputCtx };

      setStatus('Connecting');
      const sessionPromise = ai.live.connect({
        model: GEMINI_MODEL,
        config: {
          responseModalities: [Modality.AUDIO],
          systemInstruction: getAccentAdjustedInstruction('Dhaka', config),
          tools: [{
            functionDeclarations: [{
              name: 'save_lead',
              description: 'Save customer contact details.',
              parameters: {
                type: Type.OBJECT,
                properties: {
                  name: { type: Type.STRING },
                  phone: { type: Type.STRING },
                  location: { type: Type.STRING },
                  interest: { type: Type.STRING }
                },
                required: ['phone']
              }
            }]
          }]
        },
        callbacks: {
          onopen: () => {
            setIsActive(true);
            setStatus('Listening');
            const source = inputCtx.createMediaStreamSource(stream);
            const scriptProcessor = inputCtx.createScriptProcessor(4096, 1, 1);
            scriptProcessor.onaudioprocess = (e) => {
              const pcmBlob = createBlob(e.inputBuffer.getChannelData(0));
              sessionPromise.then(s => s.sendRealtimeInput({ media: pcmBlob }));
            };
            source.connect(scriptProcessor);
            scriptProcessor.connect(inputCtx.destination);
          },
          onmessage: async (msg: any) => {
            if (msg.toolCall) {
              for (const fc of msg.toolCall.functionCalls) {
                if (fc.name === 'save_lead') {
                  const newLead: Lead = {
                    id: Math.random().toString(36),
                    ...fc.args,
                    timestamp: new Date()
                  };
                  onLeadCaptured?.(newLead);
                  sessionPromise.then(s => s.sendToolResponse({
                    functionResponses: { id: fc.id, name: fc.name, response: { status: 'success' } }
                  }));
                }
              }
            }
            const base64Audio = msg.serverContent?.modelTurn?.parts?.[0]?.inlineData?.data;
            if (base64Audio) {
              setStatus('Speaking');
              nextStartTimeRef.current = Math.max(nextStartTimeRef.current, outputCtx.currentTime);
              const buffer = await decodeAudioData(decode(base64Audio), outputCtx, 24000, 1);
              const source = outputCtx.createBufferSource();
              source.buffer = buffer;
              source.connect(outputCtx.destination);
              source.onended = () => { if (status !== 'Connecting') setStatus('Listening'); };
              source.start(nextStartTimeRef.current);
              nextStartTimeRef.current += buffer.duration;
            }
          },
          onerror: () => stopSession(),
          onclose: () => stopSession()
        }
      });
      sessionPromiseRef.current = sessionPromise;
    } catch (e) {
      setStatus('Error');
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-slate-950/90 backdrop-blur-sm p-4">
      <div className="bg-white w-full max-w-lg rounded-[40px] shadow-2xl flex flex-col h-[600px] overflow-hidden">
        <div className="p-8 bg-slate-900 text-white flex items-center justify-between">
          <div className="flex items-center gap-4">
            <div className={`w-3 h-3 rounded-full ${isActive ? 'bg-green-500 animate-pulse' : 'bg-slate-500'}`}></div>
            <h2 className="font-black">Voice Session</h2>
          </div>
          <button onClick={() => { stopSession(); onClose(); }} className="text-xl opacity-50 hover:opacity-100 transition-opacity">
            <i className="fa-solid fa-times"></i>
          </button>
        </div>

        <div className="flex-1 flex flex-col items-center justify-center p-10 space-y-8 bg-slate-50">
          <div className={`w-32 h-32 rounded-full flex items-center justify-center transition-all duration-500 ${status === 'Speaking' ? 'bg-orange-500 scale-110 shadow-2xl' : 'bg-indigo-600 shadow-xl'}`}>
            <i className={`fa-solid ${status === 'Speaking' ? 'fa-volume-high' : 'fa-microphone'} text-4xl text-white`}></i>
          </div>

          <div className="text-center">
            <p className="text-[10px] font-black uppercase text-slate-400 tracking-[0.2em] mb-2">{status}</p>
            <h3 className="text-xl font-bold text-slate-800">
              {status === 'Idle' ? 'Ready to Assist' : status === 'Speaking' ? 'Agent Speaking...' : 'Listening to you...'}
            </h3>
          </div>

          {isActive && (
            <div className="flex gap-4">
              <input type="file" ref={fileInputRef} onChange={handleImageUpload} className="hidden" accept="image/*" />
              <button 
                onClick={() => fileInputRef.current?.click()}
                disabled={isSendingImage}
                className="w-14 h-14 rounded-2xl bg-white border border-slate-200 text-slate-600 hover:bg-slate-50 transition-all flex items-center justify-center shadow-sm"
              >
                {isSendingImage ? <i className="fa-solid fa-spinner fa-spin"></i> : <i className="fa-solid fa-camera"></i>}
              </button>
            </div>
          )}
        </div>

        <div className="p-8 bg-white border-t border-slate-100">
          {!isActive ? (
            <button onClick={startSession} className="w-full py-5 bg-indigo-600 text-white rounded-3xl font-black text-lg shadow-xl hover:bg-indigo-700 transition-all">
              Connect Voice Agent
            </button>
          ) : (
            <button onClick={stopSession} className="w-full py-5 bg-red-500 text-white rounded-3xl font-black text-lg shadow-xl hover:bg-red-600 transition-all">
              End Call
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default VoiceInterface;

import React, { useState, useEffect } from 'react';
import { AppView, BusinessConfig, Lead, MarketPrice } from './types';
import VoiceInterface from './components/VoiceInterface';
import { GoogleGenAI } from '@google/genai';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<AppView>(AppView.DASHBOARD);
  const [showVoiceInterface, setShowVoiceInterface] = useState(false);
  const [leads, setLeads] = useState<Lead[]>([]);
  const [marketPrices, setMarketPrices] = useState<MarketPrice[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const [config, setConfig] = useState<BusinessConfig>({
    shopName: 'Deshi Bazar',
    deliveryInsideDhaka: 60,
    deliveryOutsideDhaka: 120,
    paymentMethods: ['bKash', 'Nagad', 'Cash on Delivery'],
    returnPolicy: '7-day return if the product is defective.',
    bkashNumber: '01700000000',
    personaTone: 'friendly',
    subscriptionStatus: 'trial',
    monthlyLimit: 100,
    usageCount: 24
  });

  const handlePriceSearch = async () => {
    if (!searchQuery) return;
    setIsSearching(true);
    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });
      const response = await ai.models.generateContent({
        model: 'gemini-3-flash-preview',
        contents: `Find the current market price for "${searchQuery}" in Bangladesh from sites like Daraz or Star Tech. Return as simple markdown with link.`,
        config: { tools: [{ googleSearch: {} }] }
      });

      const newPrice: MarketPrice = {
        product: searchQuery,
        source: 'Google Search',
        price: response.text || 'Check Link',
        link: response.candidates?.[0]?.groundingMetadata?.groundingChunks?.[0]?.web?.uri || '#'
      };
      setMarketPrices(prev => [newPrice, ...prev]);
    } catch (e) {
      console.error(e);
    } finally {
      setIsSearching(false);
    }
  };

  return (
    <div className="min-h-screen flex bg-slate-50 font-['Hind_Siliguri']">
      <aside className="w-64 bg-slate-900 text-white flex flex-col p-6 gap-8">
        <h1 className="text-xl font-black italic">AMAR VOICE</h1>
        <nav className="flex flex-col gap-2">
          {[
            { view: AppView.DASHBOARD, icon: 'fa-grid-2', label: 'Overview' },
            { view: AppView.LEADS, icon: 'fa-users', label: 'Customer Leads' },
            { view: AppView.PRICE_TRACKER, icon: 'fa-magnifying-glass-chart', label: 'Price Checker' },
            { view: AppView.MARKET_INSIGHTS, icon: 'fa-chart-line', label: 'Analytics' },
            { view: AppView.SETTINGS, icon: 'fa-cog', label: 'Knowledge Base' }
          ].map(item => (
            <button key={item.view} onClick={() => setCurrentView(item.view)} className={`flex items-center gap-3 p-4 rounded-2xl transition-all ${currentView === item.view ? 'bg-indigo-600' : 'text-slate-400 hover:bg-white/5'}`}>
              <i className={`fa-solid ${item.icon} text-xs`}></i> <span className="text-sm font-bold">{item.label}</span>
            </button>
          ))}
        </nav>
      </aside>

      <main className="flex-1 flex flex-col overflow-hidden">
        <header className="h-20 bg-white border-b border-slate-200 flex items-center justify-between px-10">
          <h2 className="text-sm font-black text-slate-400 uppercase tracking-widest">{currentView}</h2>
          <button onClick={() => setShowVoiceInterface(true)} className="px-6 py-2.5 bg-indigo-600 text-white rounded-xl font-bold text-sm shadow-xl active:scale-95 transition-all">
            Launch Voice Agent
          </button>
        </header>

        <div className="flex-1 overflow-y-auto p-10">
          {currentView === AppView.DASHBOARD && (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
